// ----- 入力 -----
const replyPlain = $input.first().json.output;                    // AI が作った本文(プレーン)
const originalHtml = $('Gmail Trigger').first().json.html            // 元メール HTML
                    || $('Gmail Trigger').first().json.text;          // HTML が無ければテキスト

// ----- HTML 形式に整形 -----
const replyHtml = replyPlain
  .replace(/\n/g, '<br>');                         // 改行を <br> に

// Gmail が自動で折り畳んでくれる "gmail_quote" ブロック
const quotedHtml = `
<br><br>
<div class="gmail_quote">
  <blockquote class="gmail_quote"
    style="margin:0 0 0 .8ex;border-left:1px solid #ccc;padding-left:1ex">
    ${originalHtml}
  </blockquote>
</div>`;

// ----- 出力 -----
// itemオブジェクトを新規作成
const item = {
  message: replyHtml + quotedHtml             // Gmail ノードの message に渡す
};

return item;
