const today = new Date();

// 先月末の日付を計算（今月の1日から1日引く）
const lastMonthEnd = new Date(today.getFullYear(), today.getMonth(), 0);

// 先々月末の日付を計算（先月の1日から1日引く）
const twoMonthsAgoEnd = new Date(today.getFullYear(), today.getMonth() - 1, 0);

// フォーマット関数（元のコードと同じ形式）
function formatDate(date) {
  const day = String(date.getDate()).padStart(2, '0');
  const month = String(date.getMonth() + 1).padStart(2, '0'); // 月は0から始まるため+1
  const year = date.getFullYear();
  return `${day}-${month}-${year}`;
}

return {
  thisMonthEnd:formatDate(today),
  lastMonthEnd: formatDate(lastMonthEnd),
  twoMonthsAgoEnd: formatDate(twoMonthsAgoEnd)
};

// set_fields から値を取得
const inputData = $input.first().json;

// デバッグ
console.log('Full input data:', inputData);

const difyThisMonth  = inputData.dify_total_stars_thisMonthEnd;
const n8nThisMonth   = inputData.n8n_total_stars_thisMonthEnd;
const difyLastMonth  = inputData.dify_total_stars_lastMonthEnd;
const n8nLastMonth   = inputData.n8n_total_stars_lastMonthEnd;
const commentHTML    = inputData.comment || '';

const missing = [];
if (difyThisMonth === undefined)  missing.push('dify_total_stars_thisMonthEnd');
if (n8nThisMonth  === undefined)  missing.push('n8n_total_stars_thisMonthEnd');
if (difyLastMonth === undefined)  missing.push('dify_total_stars_lastMonthEnd');
if (n8nLastMonth  === undefined)  missing.push('n8n_total_stars_lastMonthEnd');
if (missing.length) {
  throw new Error(`Missing fields: ${missing.join(', ')}`);
}

// 差分計算
const difyDiff = difyThisMonth - difyLastMonth;
const n8nDiff  = n8nThisMonth  - n8nLastMonth;

// フォーマッタ
const formatNumber = n => (typeof n === 'number' && !isNaN(n)) ? n.toLocaleString() : '-';
const formatDiff   = d => d >= 0 ? `+${formatNumber(d)}` : formatNumber(d);

// 日付計算
const today      = new Date();
const thisMonth  = today.getMonth() + 1; // 今月（1-12）
const lastMonth  = thisMonth === 1 ? 12 : thisMonth - 1; // 先月（1-12）
const thisYear   = today.getFullYear();
const lastMonthYear = thisMonth === 1 ? thisYear - 1 : thisYear;

// タイトル用（今月を基準に）
const titleDate  = `${String(thisMonth).padStart(2, '0')}_${thisYear}_`;
const docName    = `${titleDate}Githubスター数 比較`;
const docTitle   = docName;

// ------------ 重要: Create folder ノードからフォルダIDを取得 ------------
