// ------------ Google Drive 用 multipart 本文 ------------
const boundary = 'divider';
const CRLF     = '\r\n';

const metadata = JSON.stringify({
  name    : docName,
  mimeType: 'application/vnd.google-apps.document',
  parents : [folderId], // 動的に取得したフォルダIDを使用
});

// マルチパート組み立て（CRLF を実文字）
let body  = `--${boundary}${CRLF}`;
body     += `Content-Type: application/json; charset=UTF-8${CRLF}${CRLF}`;
body     += `${metadata}${CRLF}`;
body     += `--${boundary}${CRLF}`;
body     += `Content-Type: text/html${CRLF}${CRLF}`;
body     += `${htmlDocument}${CRLF}`;
body     += `--${boundary}--${CRLF}`;   // ← 終端境界

return {
  rawData     : body,
  documentName: docName,
  titleDate   : titleDate,
  folderId    : folderId,
  debugInfo   : {
    n8nThisMonth, difyThisMonth, n8nDiff, difyDiff,
    thisMonth, lastMonth,
    bodyLength : body.length,
    boundaryOK : body.includes(`--${boundary}--${CRLF}`),
    folderInfo : folderData,
    hasComment : !!commentHTML
  },
};
